/**
 * FULL UPDATED Course Processor Node
 * Replace your existing Course Processor node's JS with this in n8n.
 */

const original               = $json;
const records                = original.records || [];
const userQuery              = (original.conversation_context?.course_query || '').toLowerCase();
const searchTerm             = userQuery || (original.course_mentioned_normalized || '').toLowerCase();

console.log('=== FIXED COURSE PROCESSOR DEBUG ===');
console.log('Records found:', records.length);
console.log('User query:', userQuery);
console.log('Search term used:', searchTerm);

let course_matches      = [];
let unscheduled_courses = [];
let selected_course     = original.conversation_context?.selected_course || null;
let has_multiple        = false;
let has_unscheduled     = false;
let conversation_context = { ...(original.conversation_context || {}) };

// helper: parse Spanish human date strings into YYYY-MM-DD (best-effort)
function parseFecha(fechaStr) {
  if (!fechaStr || typeof fechaStr !== 'string') return null;
  const lower = fechaStr.toLowerCase();
  if (lower.includes('por confirmar') || lower.includes('por definir')) return null;
  const months = {
    enero:1, febrero:2, marzo:3, abril:4, mayo:5, junio:6,
    julio:7, agosto:8, septiembre:9, octubre:10, noviembre:11, diciembre:12
  };
  const yearMatch = lower.match(/(20\d{2})/);
  const year = yearMatch ? yearMatch[1] : `${new Date().getFullYear()}`;
  let month = null;
  for (const m in months) {
    if (lower.includes(m)) { month = months[m]; break; }
  }
  if (!month) return null;
  let day = '01';
  const dayMatch = lower.match(/(\d{1,2})/);
  if (dayMatch) {
    const n = parseInt(dayMatch[1], 10);
    if (n >= 1 && n <= 31) day = String(n).padStart(2,'0');
  }
  const iso = `${year}-${String(month).padStart(2,'0')}-${day}`;
  return isNaN(new Date(iso).getTime()) ? null : iso;
}

// ðŸ”§ FIXED price-formatting helper: keep original data, let display handlers format
function formatStoredPrice(price) {
  console.log('formatStoredPrice input:', price, 'type:', typeof price);

  if (!price || price === 'Consultar' || price === 'Por confirmar') {
    return price;
  }

  // Keep the original price data as-is for downstream formatting
  return price;
}

// ðŸ”§ PRICE DEBUGGING - Add comprehensive logging
console.log('=== PRICE DEBUGGING ===');
console.log('Total records found:', records.length);

if (records.length > 0) {
  records.slice(0, 3).forEach((record, i) => {
    console.log(`Record ${i+1}:`, {
      nombre: record.nombre,
      codigo: record.codigo,
      precio: record.precio,
      precio_type: typeof record.precio,
      discounted_price: record.discounted_price,
      discounted_price_type: typeof record.discounted_price,
      raw_record_keys: Object.keys(record)
    });
  });
}

if (records.length > 0 && searchTerm.length > 0) {
  function scoreMatch(course, searchTerm) {
    const name   = (course.nombre || '').toLowerCase();
    const codigo = (course.codigo || '').toLowerCase();
    // Remove strict * exclusion; instead, penalize but allow unscheduled
    if (codigo.includes('*')) return 100;  // Lower base score for placeholders, but allow

    const cleanSearchTerm = searchTerm.normalize('NFD').replace(/[\u0300-\u036f]/g, '');
    const cleanName       = name.normalize('NFD').replace(/[\u0300-\u036f]/g, '');
    if (cleanName.includes(cleanSearchTerm)) return 1000;

    // Expand semantic synonyms for "psm" and similar
    const sem = {
      psm: ['seguridad', 'hse', 'sst', 'riesgo', 'process safety', 'gestion de procesos', 'management'],
      ductos:  ['oleoductos','gasoductos','tuberias','pipelines'],
      seguridad:['psm','hse','sst','riesgo'],
      riesgo:   ['seguridad','psm','hse','sst','riesgos']
      // Add more if needed, e.g., tuberias: ['ductos', ...]
    };

    const searchWords = cleanSearchTerm.split(/\s+/).filter(w => w.length > 1);  // Loosen to >1 char
    const nameWords   = cleanName.split(/\s+/).filter(w => w.length > 1);
    let score = 0;

    if (searchWords.length > 1) {
      let matched = 0, total = searchWords.length;
      const connectors = ['de','del','la','el','en','con','para','por','como','que','las','los'];
      for (const w of searchWords) {
        if (connectors.includes(w)) { total--; continue; }
        for (const n of nameWords) {
          if (
            n === w ||
            (n.length >= 4 && w.length >= 4 && (n.includes(w) || w.includes(n))) ||
            n.startsWith(w) ||
            w.startsWith(n) ||
            sem[w]?.includes(n) ||
            sem[n]?.includes(w)
          ) {
            matched++;
            break;
          }
        }
      }
      if (total > 0) {
        const r = matched / total;
        if (r >= 0.8) score = 500;
        else if (r >= 0.5) score = 300;
        else return 0;
      }
    } else if (searchWords.length === 1) {
      const w = searchWords[0];
      const sem1 = {
        psm: ['seguridad', 'hse', 'sst', 'riesgo', 'process safety', 'gestion de procesos', 'management'],
        tuberias:['oleoductos','gasoductos','ductos','pipelines'],
        ductos:  ['oleoductos','gasoductos','tuberias','pipelines'],
        seguridad:['psm','hse','sst','riesgo'],
        riesgo:   ['seguridad','psm','hse','sst','riesgos']
      };
      let found = false;
      for (const n of nameWords) {
        if (
          n === w ||
          (n.length >= 4 && w.length >= 4 && (n.includes(w) || w.includes(n))) ||
          n.startsWith(w) ||
          w.startsWith(n) ||
          sem1[w]?.includes(n)
        ) {
          score = 300;
          found = true;
          break;
        }
      }
      if (!found) return 0;
    }

    if (score > 0) {
      if (
        course.fecha &&
        !course.fecha.toLowerCase().includes('por confirmar') &&
        course.fecha.includes('2025')
      ) {
        score += 50;
      }
      if (codigo.startsWith('m')) score += 20;
    }

    // Lower threshold for short queries
    if (searchTerm.length <= 3 && score > 0) score += 200;  // Boost acronyms like "psm"

    return score;
  }

  function isScheduled(course) {
    return (
      course.fecha &&
      !course.fecha.toLowerCase().includes('por confirmar') &&
      !course.fecha.toLowerCase().includes('por definir')
    );
  }

  function getDateSortValue(course) {
    if (!course.fecha) return 999999;
    const f = course.fecha.toLowerCase();
    if (f.includes('por confirmar') || f.includes('por definir')) return 999999;
    const months = {
      enero:1, febrero:2, marzo:3, abril:4, mayo:5, junio:6,
      julio:7, agosto:8, septiembre:9, octubre:10, noviembre:11, diciembre:12
    };
    for (const m in months) {
      if (f.includes(m)) return months[m];
    }
    return 999999;
  }

  const scored = records
    .map(c => ({
      course: c,
      score: scoreMatch(c, searchTerm),
      isScheduled: isScheduled(c),
      dateSortValue: getDateSortValue(c)
    }))
    .filter(x => x.score >= 150);  // Lowered from 200

  const scheduled = scored
    .filter(x => x.isScheduled)
    .sort((a, b) => b.score - a.score || a.dateSortValue - b.dateSortValue);

  const unscheduledData = scored
    .filter(x => !x.isScheduled)
    .sort((a, b) => b.score - a.score);

  if (scheduled.length > 0) {
    course_matches = scheduled.slice(0,5).map(x => {
      const iso = parseFecha(x.course.fecha);

      console.log('Processing course for output:', {
        nombre: x.course.nombre,
        raw_precio: x.course.precio,
        raw_discounted: x.course.discounted_price,
        formatted_precio: formatStoredPrice(x.course.precio),
        formatted_discounted: formatStoredPrice(x.course.discounted_price)
      });

      return {
        codigo:           x.course.codigo,
        nombre:           x.course.nombre,
        modalidad:        x.course.modalidad,
        fecha:            x.course.fecha,
        ciudad:           x.course.ciudad,
        duracion:         x.course.duracion || x.course.intensidad,
        precio:           formatStoredPrice(x.course.precio) || 'Consultar',
        discounted_price: formatStoredPrice(x.course.discounted_price) || formatStoredPrice(x.course.precio) || 'Consultar',
        instructor:       x.course.instructor || '',
        reserva_link:     x.course.reserva_link || '',
        url:              x.course.url,
        fecha_formatted:  x.course.fecha || 'Por definir',
        fecha_iso:        iso
      };
    });
    has_multiple = scheduled.length > 1;
  }

  if (unscheduledData.length > 0) {
    unscheduled_courses = unscheduledData.slice(0,5).map(x => {
      const iso = parseFecha(x.course.fecha);
      return {
        codigo:           x.course.codigo,
        nombre:           x.course.nombre,
        modalidad:        x.course.modalidad,
        fecha:            x.course.fecha,
        ciudad:           x.course.ciudad,
        duracion:         x.course.duracion || x.course.intensidad,
        precio:           formatStoredPrice(x.course.precio) || 'Consultar',
        discounted_price: formatStoredPrice(x.course.discounted_price) || formatStoredPrice(x.course.precio) || 'Consultar',
        instructor:       x.course.instructor || '',
        reserva_link:     x.course.reserva_link || '',
        url:              x.course.url,
        fecha_formatted:  x.course.fecha || 'Por confirmar',
        fecha_iso:        iso
      };
    });
    has_unscheduled = true;
  }
}

// update context
conversation_context.course_matches       = course_matches;
conversation_context.unscheduled_courses  = unscheduled_courses;
conversation_context.has_multiple         = has_multiple;
conversation_context.has_unscheduled      = has_unscheduled;
conversation_context.should_search_course = false;

console.log('FINAL RESULTS:');
console.log('- Scheduled courses:', course_matches.length);
console.log('- Unscheduled courses:', unscheduled_courses.length);
console.log('- Selected course:', selected_course?.nombre || 'None');

return {
  json: {
    ...$json,
    conversation_context,
    selected_course: conversation_context.selected_course || null,
    course_matches,
    unscheduled_courses,
    has_multiple,
    has_unscheduled
  }
};

// Enhanced Message Router - Filters out non-message webhook events

console.log('=== ENHANCED MESSAGE ROUTER DEBUG ===');
console.log('Full webhook payload:', JSON.stringify($json, null, 2));

function formatPhoneE164(rawPhone) {
  if (!rawPhone) return null;
  const digits = rawPhone.replace(/\D/g, '');
  return rawPhone.startsWith('+') ? rawPhone : `+${digits}`;
}

function detectMessageType(jsonData) {
  const msg = jsonData.messages?.[0];
  if (!msg) return 'no_message';
  if (msg.interactive?.button_reply) return 'button';
  if (msg.interactive?.list_reply) return 'list';
  if (msg.text?.body) return 'text';
  return 'unsupported';
}

function extractContent(jsonData) {
  const msg = jsonData.messages?.[0];
  if (!msg) return '';
  return (
    msg.text?.body ||
    msg.interactive?.button_reply?.id ||
    msg.interactive?.list_reply?.id ||
    ''
  );
}

function isNightShift() {
  const hour = new Date().getHours();
  return hour >= 22 || hour <= 6;
}

const jsonData = $json;

try {
  // ðŸ”§ ENHANCED FILTERING: Check for various non-message webhook types

  // Skip if this is a status update webhook
  if (jsonData.statuses && jsonData.statuses.length > 0) {
    console.log('ðŸš« SKIPPING: This is a message status update webhook');
    return { json: { skip: true, reason: 'status_update', skip_sending: true } };
  }

  // Skip if this is a contacts update
  if (jsonData.contacts && jsonData.contacts.length > 0 && !jsonData.messages) {
    console.log('ðŸš« SKIPPING: This is a contacts update webhook');
    return { json: { skip: true, reason: 'contacts_update', skip_sending: true } };
  }

  // Skip if no messages array
  if (!jsonData?.messages?.length) {
    console.log('ðŸš« SKIPPING: No messages in webhook payload');
    return { json: { skip: true, reason: 'no_messages', skip_sending: true } };
  }

  const messageType = detectMessageType(jsonData);
  if (messageType === 'unsupported' || messageType === 'no_message') {
    console.log('ðŸš« SKIPPING: Unsupported message type:', messageType);
    return { json: { skip: true, reason: messageType, skip_sending: true } };
  }

  const phone_e164 = formatPhoneE164(
    jsonData.contacts?.[0]?.wa_id || jsonData.messages?.[0]?.from
  );
  if (!phone_e164) {
    console.log('ðŸš« SKIPPING: No valid phone number found');
    return { json: { skip: true, reason: 'no_phone', skip_sending: true } };
  }

  // ðŸ”§ PRESERVE ORIGINAL CASE: Extract content in original case AND lowercase
  const originalContent = extractContent(jsonData).trim(); // Keep original case
  const content = originalContent.toLowerCase(); // Lowercase for processing
  const userName = jsonData.contacts?.[0]?.profile?.name || 'Usuario';
  const currentTimestamp = new Date().toISOString();

  // flag for topâ€‘level menu selection
  let initial_option = null;

  // preserve selection context: when awaiting a courseâ€‘list index, do NOT treat 1â€“4 as menu
  const awaitingSelection = jsonData.conversation_context?.awaiting_selection || false;

  console.log('=== VALID MESSAGE DETECTED ===');
  console.log('Message type:', messageType);
  console.log('Original content:', originalContent);
  console.log('Lowercase content:', content);
  console.log('Phone:', phone_e164);
  console.log('Awaiting selection state:', awaitingSelection);

  // only map 1â€“4 to initial_option when NOT in the middle of a course selection
  if (!awaitingSelection && /^[1-4]$/.test(content)) {
    initial_option = parseInt(content, 10);
    console.log('âœ… Detected topâ€‘level menu number:', initial_option);
  }

  return {
    json: {
      ...jsonData,
      messageType,
      phone_e164,
      content, // Lowercase for processing
      originalContent, // ðŸ”§ NEW: Original case preserved
      userName,
      timestamp: currentTimestamp,
      isNightShift: isNightShift(),
      ready_for_ai: true,
      initial_option,
    },
  };
} catch (error) {
  console.log('âŒ ERROR in Message Router:', error.message);
  return {
    json: {
      ...$json,
      response_message: 'Error interno, intente de nuevo.',
      should_send_buttons: false,
      error_details: error.message,
    },
  };
}

// ðŸ”§ ENHANCED Context Enricher â€” Bulletproof data preservation
function calculateElitePriority(item) {
  let score = Number(item.urgency_score) || 20;
  const keywords = item.keywords_found || [];
  if (keywords.includes('inscripcion')) score += 40;
  if (keywords.includes('urgente')) score += 25;
  if (item.isNightShift) score += 20;
  if (item.has_course_info) score += 25;
  score = Math.min(score, 100);
  const priority = score >= 70 ? 'hot' : score >= 50 ? 'warm' : 'cold';
  return { score, priority };
}

try {
  const priorityData = calculateElitePriority($json);
  const shouldAlertFelipe = priorityData.priority === 'hot' || $json.intent === 'inscripcion';
  let conversation_context = $json.conversation_context || {};

  console.log('=== ENHANCED CONTEXT ENRICHER DEBUG ===');
  console.log('Input conversation_context type:', typeof conversation_context);
  console.log('Available data keys:', Object.keys($json));

  // Ensure conversation_context is an object
  if (typeof conversation_context === 'string') {
    try {
      conversation_context = JSON.parse(conversation_context);
    } catch (e) {
      console.log('âŒ Failed to parse conversation_context in enricher:', e.message);
      conversation_context = {};
    }
  }
  if (typeof conversation_context !== 'object' || conversation_context === null) {
    conversation_context = {};
  }

  // ðŸ”§ FIXED: Only add name and phone if parsed AND not already set
  if ($json.parsed_name && !conversation_context.user_name) {
    conversation_context.user_name = $json.parsed_name;
    console.log('âœ… Set user_name from parsed_name:', $json.parsed_name);
  } else if ($json.parsed_name && conversation_context.user_name) {
    console.log('âš ï¸ NOT overwriting existing user_name:', conversation_context.user_name, 'with parsed_name:', $json.parsed_name);
  }

  if ($json.parsed_phone && !conversation_context.user_phone) {
    conversation_context.user_phone = $json.parsed_phone;
    console.log('âœ… Set user_phone from parsed_phone:', $json.parsed_phone);
  } else if ($json.parsed_phone && conversation_context.user_phone) {
    console.log('âš ï¸ NOT overwriting existing user_phone:', conversation_context.user_phone, 'with parsed_phone:', $json.parsed_phone);
  }

  if (Array.isArray($json.keywords_found)) {
    if ($json.keywords_found.includes('price_blocker')) {
      conversation_context.conversion_blockers = [
        ...(conversation_context.conversion_blockers || []),
        'price'
      ].filter((v,i,a) => a.indexOf(v) === i);
    }
    if ($json.keywords_found.includes('prefer_call'))  conversation_context.preferred_contact = 'call';
    if ($json.keywords_found.includes('prefer_email')) conversation_context.preferred_contact = 'email';
  }

  // ðŸ”§ BULLETPROOF COURSE DATA PRESERVATION
  console.log('=== BULLETPROOF DATA PRESERVATION ===');
  function preserveArraySafely(fieldName, sourceData) {
    console.log(`Preserving ${fieldName}:`);
    console.log('  Source type:', typeof sourceData);
    console.log('  Source length:', Array.isArray(sourceData) ? sourceData.length : 'N/A');
    console.log('  Existing type:', typeof conversation_context[fieldName]);
    console.log('  Existing length:', Array.isArray(conversation_context[fieldName]) ? conversation_context[fieldName].length : 'N/A');

    if (Array.isArray(sourceData) && sourceData.length > 0) {
      conversation_context[fieldName] = sourceData;
      console.log(`  âœ… Used fresh ${fieldName} data (${sourceData.length} items)`);
    } else if (Array.isArray(conversation_context[fieldName]) && conversation_context[fieldName].length > 0) {
      console.log(`  âœ… Kept existing ${fieldName} data (${conversation_context[fieldName].length} items)`);
    } else {
      conversation_context[fieldName] = [];
      console.log(`  âš ï¸ Initialized empty ${fieldName} array`);
    }
  }

  preserveArraySafely('course_matches',       $json.course_matches);
  preserveArraySafely('unscheduled_courses',  $json.unscheduled_courses);

  // Preserve boolean flags with fallbacks
  if ($json.has_multiple !== undefined) {
    conversation_context.has_multiple = $json.has_multiple;
  } else if (conversation_context.has_multiple === undefined) {
    conversation_context.has_multiple = (conversation_context.course_matches.length || 0) > 1;
  }

  if ($json.has_unscheduled !== undefined) {
    conversation_context.has_unscheduled = $json.has_unscheduled;
  } else if (conversation_context.has_unscheduled === undefined) {
    conversation_context.has_unscheduled = (conversation_context.unscheduled_courses.length || 0) > 0;
  }

  // Preserve selected course
  if ($json.selected_course && typeof $json.selected_course === 'object') {
    conversation_context.selected_course = $json.selected_course;
    console.log('âœ… Preserved selected_course:', $json.selected_course.nombre);
  } else if (!conversation_context.selected_course) {
    console.log('âš ï¸ No selected_course data available');
  }

  // ðŸ”§ VALIDATION: Ensure data integrity
  function validateDataIntegrity() {
    const issues = [];

    if (!Array.isArray(conversation_context.course_matches)) {
      conversation_context.course_matches = [];
      issues.push('Fixed course_matches array');
    }
    if (!Array.isArray(conversation_context.unscheduled_courses)) {
      conversation_context.unscheduled_courses = [];
      issues.push('Fixed unscheduled_courses array');
    }
    if (!Array.isArray(conversation_context.conversion_blockers)) {
      conversation_context.conversion_blockers = [];
      issues.push('Fixed conversion_blockers array');
    }

    conversation_context.has_multiple    = (conversation_context.course_matches.length || 0) > 1;
    conversation_context.has_unscheduled = (conversation_context.unscheduled_courses.length || 0) > 0;

    if (issues.length > 0) {
      console.log('ðŸ”§ Fixed data integrity issues:', issues);
    } else {
      console.log('âœ… Data integrity validated');
    }
  }
  validateDataIntegrity();

  console.log('=== FINAL ENRICHED STATE ===');
  console.log('course_matches count:',      conversation_context.course_matches.length);
  console.log('unscheduled_courses count:', conversation_context.unscheduled_courses.length);
  console.log('has_multiple:',              conversation_context.has_multiple);
  console.log('has_unscheduled:',           conversation_context.has_unscheduled);
  console.log('selected_course:',           conversation_context.selected_course?.nombre || 'None');
  console.log('user_name preserved:',       conversation_context.user_name || 'None');

  return {
    json: {
      ...$json,
      elite_score:            priorityData.score,
      elite_priority:         priorityData.priority,
      should_alert_felipe:    shouldAlertFelipe,
      is_returning:           false,
      conversation_context,
      selected_course:        conversation_context.selected_course || null,
      course_matches:         conversation_context.course_matches,
      unscheduled_courses:    conversation_context.unscheduled_courses,
      has_multiple:           conversation_context.has_multiple,
      has_unscheduled:        conversation_context.has_unscheduled
    }
  };
} catch (error) {
  console.log('âŒ Error in Context Enricher:', error.message);
  return {
    json: {
      ...$json,
      response_message:      'Error interno, intente de nuevo.',
      should_send_buttons:   false,
      error_details:         error.message,
      conversation_context: {
        course_matches:      [],
        unscheduled_courses: [],
        conversion_blockers: [],
        has_multiple:        false,
        has_unscheduled:     false
      }
    }
  };
}

// ðŸ”§ Node 5: Data Preservation & Context Updater
// Purpose: Final context cleanup, validation, and data preservation

let conversation_context = { ...$json.conversation_context };
const conversation_step = $json.conversation_step;

console.log('=== DATA PRESERVATION & UPDATER DEBUG ===');
console.log('Input context keys:', Object.keys(conversation_context));
console.log('Conversation step:', conversation_step);

// ðŸ”§ ENSURE CRITICAL ARRAYS EXIST AND ARE VALID
function validateAndPreserveArrays() {
  // Preserve course_matches
  if (!Array.isArray(conversation_context.course_matches)) {
    conversation_context.course_matches = $json.course_matches || [];
  }

  // Preserve unscheduled_courses
  if (!Array.isArray(conversation_context.unscheduled_courses)) {
    conversation_context.unscheduled_courses = $json.unscheduled_courses || [];
  }

  // Preserve conversion_blockers
  if (!Array.isArray(conversation_context.conversion_blockers)) {
    conversation_context.conversion_blockers = [];
  }

  console.log('âœ… Array validation complete:');
  console.log('- course_matches:', conversation_context.course_matches.length);
  console.log('- unscheduled_courses:', conversation_context.unscheduled_courses.length);
  console.log('- conversion_blockers:', conversation_context.conversion_blockers.length);
}

// ðŸ”§ ENSURE BOOLEAN FLAGS ARE CONSISTENT
function validateBooleanFlags() {
  // Update has_multiple based on actual course_matches length
  conversation_context.has_multiple = (conversation_context.course_matches?.length || 0) > 1;

  // Update has_unscheduled based on actual unscheduled_courses length
  conversation_context.has_unscheduled = (conversation_context.unscheduled_courses?.length || 0) > 0;

  console.log('âœ… Boolean flags updated:');
  console.log('- has_multiple:', conversation_context.has_multiple);
  console.log('- has_unscheduled:', conversation_context.has_unscheduled);
}

// ðŸ”§ PRESERVE SELECTED COURSE DATA
function preserveSelectedCourse() {
  // Keep existing selected_course if it exists and is valid
