/** Capitalize each word in a full name */
function capitalizeName(full) {
  return full
    .split(' ')
    .filter(Boolean)
    .map(w => w.charAt(0).toUpperCase() + w.slice(1).toLowerCase())
    .join(' ');
}

/** Parse "name, phone, participants" variants */
function parseNamePhoneParticipants(txt) {
  if (!txt) return { name: '', phone: '', participants: 1 };
  const comma = txt.split(',').map(s => s.trim()).filter(Boolean);
  if (comma.length >= 2) {
    let [rawName, rawPhone, rawPart] = comma;
    let participants = rawPart?.match(/(\d+)/)?.[1] || 1;
    return {
      name: capitalizeName(rawName),
      phone: rawPhone,
      participants: +participants
    };
  }
  let name = '', phone = '', participants = 1;
  const phoneRx = /[\+]?[\d\s\-\(\)]{8,}/;
  txt.split(/\n|  +/)
    .map(s => s.trim())
    .filter(Boolean)
    .forEach(line => {
      if (phoneRx.test(line)) phone = line.replace(/\s+/g, '');
      else if (line.length > 2) name += (name ? ' ' : '') + line;
    });
  participants = +txt.match(/(\d+)\s*persona[s]?/i)?.[1] || 1;
  if ((!name || !phone) && txt.includes(' ')) {
    const words = txt.split(/\s+/);
    const idx = words.findIndex(w => phoneRx.test(w));
    if (idx > 0) {
      name = words.slice(0, idx).join(' ');
      phone = words.slice(idx).join('').trim();
    }
  }
  return {
    name: name ? capitalizeName(name) : txt,
    phone,
    participants
  };
}

/** Parse "name + course" */
function parseNameAndCourse(raw) {
  if (!raw) return { name: '', course: '' };
  console.log('parseNameAndCourse input:', raw);
  if (raw.includes(',')) {
    const parts = raw.split(',').map(s => s.trim()).filter(Boolean);
    if (parts.length >= 2) {
      return {
        name: capitalizeName(parts[0]),
        course: parts[1]
      };
    }
  }
  const lines = raw.split(/\n+/).map(s => s.trim()).filter(Boolean);
  if (lines.length >= 2) return { name: capitalizeName(lines[0]), course: lines.slice(1).join(' ') };
  const keywords = ['curso','capacitaciÃ³n','entrenamiento','seguridad','gerencia','riesgo'];
  const words = raw.split(/\s+/);
  const idx = words.findIndex(w => keywords.some(k => w.toLowerCase().includes(k)));
  if (idx > 0) return { name: capitalizeName(words.slice(0,idx).join(' ')), course: words.slice(idx).join(' ') };
  if (words.length > 2) return { name: capitalizeName(words.slice(0,2).join(' ')), course: words.slice(2).join(' ') };
  return { name: capitalizeName(raw), course: '' };
}

/** Parse "name + course + request" */
function parseNameCourseAndRequest(raw) {
  if (!raw) return { name: '', course: '', request: '' };
  console.log('parseNameCourseAndRequest input:', raw);
  if (raw.includes(',')) {
    const parts = raw.split(',').map(s => s.trim()).filter(Boolean);
    if (parts.length >= 3) {
      return {
        name: capitalizeName(parts[0]),
        course: parts[1], 
        request: parts.slice(2).join(', ')
      };
    } else if (parts.length === 2) {
      const rk = ['requiero','necesito','solicito','quiero','pido','ayuda','problema','impago','soporte','asistencia'];
      const second = parts[1].toLowerCase();
      for (let k of rk) {
        if (second.includes(k)) {
          const courseKeywords = ['curso','capacitaciÃ³n','entrenamiento','seguridad','gerencia','riesgo','soldadura','tuberÃ­as','ductos','psm'];
          let foundCourse = '';
          let foundRequest = parts[1];
          for (let ck of courseKeywords) {
            if (second.includes(ck)) {
              const ckIndex = second.indexOf(ck);
              const beforeKeyword = parts[1].substring(0, ckIndex + ck.length).trim();
              const afterKeyword = parts[1].substring(ckIndex + ck.length).trim();
              if (beforeKeyword && afterKeyword) {
                foundCourse = beforeKeyword;
                foundRequest = afterKeyword;
                break;
              }
            }
          }
          return {
            name: capitalizeName(parts[0]),
            course: foundCourse || '',
            request: foundRequest
          };
        }
      }
      return {
        name: capitalizeName(parts[0]),
        course: parts[1],
        request: ''
      };
    }
  }
  const lines = raw.split(/\n+/).map(s=>s.trim()).filter(Boolean);
  if (lines.length >= 3) {
    return { 
      name: capitalizeName(lines[0]), 
      course: lines[1], 
      request: lines.slice(2).join(' ') 
    };
  }
  if (lines.length === 2) {
    const [name, second] = lines;
    const rk = ['requiero','necesito','solicito','quiero','pido','ayuda','problema','impago'];
    const low = second.toLowerCase();
    for (let k of rk) {
      const i = low.indexOf(k);
      if (i > 0) {
        return {
          name: capitalizeName(name),
          course: second.substring(0,i).trim(),
          request: second.substring(i).trim()
        };
      }
    }
    return { name: capitalizeName(name), course: second, request: '' };
  }
  return { name: capitalizeName(raw), course: '', request: '' };
}

