// ðŸ”§ Enhanced Cooldown & Anti-Duplication Handler WITH FIXED RESET FUNCTIONALITY
// Purpose: Handle cooldown logic, prevent duplicates, AND properly detect reset conditions

const content = $json.content;
const conversation_step = $json.conversation_step;
const conversation_context = $json.conversation_context || {};

// ðŸ”§ RESET CONSTANTS
const CONVERSATION_RESET_MINUTES = 3;
const CONVERSATION_COOLDOWN_SECONDS = 45;
const RESPONSE_COOLDOWN_SECONDS = 4;
const MESSAGE_PROCESSING_COOLDOWN_SECONDS = 3;
const RAPID_GREETING_COOLDOWN_SECONDS = 30;

const ACKNOWLEDGMENT_WORDS = [
  'gracias', 'ok', 'perfecto', 'bien', 'entendido', 'vale', 'listo',
  'excelente', 'muchas gracias', 'estÃ¡ bien', 'muy bien', 'genial',
  'bueno', 'si', 'sÃ­', 'claro', 'de acuerdo', 'correcto', 'dale'
];

// ðŸ”§ GREETING DETECTION
function isGreetingMessage(content) {
  const greetingPatterns = /^(hola|buenos dÃ­as|buenas tardes|buenas noches|buen dÃ­a)$/i;
  return greetingPatterns.test(content.toLowerCase().trim());
}

// ðŸ”§ FIXED: RESET CONDITION DETECTION
function shouldResetConversation(context, currentTimestamp) {
  console.log('ðŸ”„ CHECKING RESET CONDITIONS...');

  // 1. Check for conversation end conditions
  if (context.end_conversation === true) {
    console.log('ðŸ”„ RESET TRIGGER: end_conversation flag set');
    return { shouldReset: true, reason: 'conversation_ended' };
  }

  if (context.handoff === true) {
    console.log('ðŸ”„ RESET TRIGGER: handoff to advisor completed');
    return { shouldReset: true, reason: 'handoff_completed' };
  }

  // 2. ðŸ”§ CRITICAL FIX: Check for post-reset scenarios more comprehensively
  if (context.reset_from_previous === true || context.pending_reset === true) {
    console.log('ðŸ”„ RESET TRIGGER: post-reset scenario detected');
    return { shouldReset: true, reason: context.reset_reason || 'post_reset_detected' };
  }

  // 3. Check if we're in a state where conversation should be reset (handoff/end but no active step)
  if ((context.handoff === true || context.end_conversation === true) && !$json.conversation_step) {
    console.log('ðŸ”„ RESET TRIGGER: completed conversation state detected');
    return { shouldReset: true, reason: 'completed_conversation_state' };
  }

  // 4. Check for conversation_ended_at timestamp indicating completed conversation
  if (context.conversation_ended_at && !$json.conversation_step) {
    console.log('ðŸ”„ RESET TRIGGER: conversation_ended_at timestamp exists');
    return { shouldReset: true, reason: 'conversation_ended_timestamp' };
  }

  // 5. Check for 3-minute inactivity
  if (context.last_user_message_time) {
    const lastMessageTime = new Date(context.last_user_message_time);
    const currentTime = new Date(currentTimestamp);
    const minutesSinceLastMessage = (currentTime - lastMessageTime) / (1000 * 60);

    console.log('ðŸ•˜ Time check:', {
      lastMessage: context.last_user_message_time,
      current: currentTimestamp,
      minutesElapsed: minutesSinceLastMessage.toFixed(2)
    });

    if (minutesSinceLastMessage >= CONVERSATION_RESET_MINUTES) {
      console.log('ðŸ”„ RESET TRIGGER: 3+ minutes of inactivity');
      return { shouldReset: true, reason: 'inactivity_timeout' };
    }
  }

  return { shouldReset: false, reason: null };
}

// ðŸ”§ FIXED: Enhanced duplicate greeting detection (with proper reset awareness)
function isRapidOrDuplicateGreeting(context, currentTime) {
  if (!isGreetingMessage(content)) {
    return false;
  }

  console.log('ðŸ” GREETING DETECTION:');
  console.log('- Content:', content);
