  console.log('- Current conversation step:', $json.conversation_step);

  // ðŸ”§ CRITICAL FIX: Allow greetings during ANY reset scenario
  const isInResetScenario = context.pending_reset || 
                           context.reset_from_previous ||
                           (context.handoff === true && !$json.conversation_step) ||
                           (context.end_conversation === true && !$json.conversation_step) ||
                           (context.conversation_ended_at && !$json.conversation_step);

  if (isInResetScenario) {
    console.log('âœ… GREETING ALLOWED - Reset scenario detected');
    return false;
  }

  // If intro was already sent AND we're not in a reset scenario, check for rapid duplicates
  if (context.intro_sent && !isInResetScenario) {
    console.log('ðŸ” Intro already sent, checking for rapid duplicates...');

    const currentTimeObj = new Date(currentTime);

    // Check last_greeting_time for rapid duplicates
    if (context.last_greeting_time) {
      const lastGreetingTime = new Date(context.last_greeting_time);
      const secondsSinceLastGreeting = (currentTimeObj - lastGreetingTime) / 1000;

      if (secondsSinceLastGreeting < RAPID_GREETING_COOLDOWN_SECONDS) {
        console.log(`ðŸš« RAPID DUPLICATE: only ${secondsSinceLastGreeting.toFixed(1)}s since last greeting`);
        return true;
      }
    }

    // Check last_response_at for recent bot activity
    if (context.last_response_at) {
      const lastResponseTime = new Date(context.last_response_at);
      const secondsSinceLastResponse = (currentTimeObj - lastResponseTime) / 1000;

      if (secondsSinceLastResponse < RAPID_GREETING_COOLDOWN_SECONDS) {
        console.log(`ðŸš« RAPID DUPLICATE: only ${secondsSinceLastResponse.toFixed(1)}s since last response`);
        return true;
      }
    }

    console.log('âš ï¸ DUPLICATE GREETING: intro already sent');
    return true; // Block duplicate greetings when intro was already sent
  }

  console.log('âœ… FIRST GREETING OR POST-RESET GREETING ALLOWED');
  return false;
}

// ðŸ”§ COOLDOWN FUNCTIONS
function isInConversationCooldown(context) {
  if (!context.conversation_ended_at) return false;
  const endTime = new Date(context.conversation_ended_at);
  const now = new Date();
  const secondsPassed = (now - endTime) / 1000;
  return secondsPassed < CONVERSATION_COOLDOWN_SECONDS;
}

function isInResponseCooldown(context) {
  if (!context.last_response_at) return false;
  const responseTime = new Date(context.last_response_at);
  const now = new Date();
  const secondsPassed = (now - responseTime) / 1000;
  return secondsPassed < RESPONSE_COOLDOWN_SECONDS;
}

