const conversation_step = $json.conversation_step || '';
const timestamp = new Date($json.timestamp || Date.now());

// ðŸ”§ CRITICAL FIX: Check if this is a reset scenario - start fresh history
const isResetScenario = $json.should_reset_conversation || 
                       $json.conversation_context?.reset_from_previous ||
                       $json.post_send_reset_triggered ||
                       $json.conversation_context?.pending_reset ||
                       $json.new_record;

let existing_history = '';

if (!isResetScenario) {
  // Normal scenario: get existing conversation history
  existing_history = $json.conversation_history || 
                    ($json.records && $json.records.length > 0 ? 
                     $json.records[0].conversation_history || '' : '');
} else {
