    post_send_reset_triggered: $json.post_send_reset_triggered,
    pending_reset: $json.conversation_context?.pending_reset,
    new_record: $json.new_record
  });
  existing_history = '';
}

console.log('=== ENHANCED CONVERSATION HISTORY BUILDER ===');
console.log('Is reset scenario:', isResetScenario);
console.log('Starting with existing history length:', existing_history.length);

// Format timestamp
const formatted_time = timestamp.toLocaleString('es-CO', {
  year: 'numeric',
  month: '2-digit',
  day: '2-digit', 
  hour: '2-digit',
  minute: '2-digit',
  hour12: false
});

let new_entries = [];

// Add user message with context
if (current_message && current_message.trim() !== '') {
  let user_entry = `[${formatted_time}] Usuario: ${current_message.trim()}`;

  // Add context if available
  if (conversation_step) {
    user_entry += ` (${conversation_step})`;
  }

  new_entries.push(user_entry);
}

// Add bot response with type
if (response_message && response_message.trim() !== '' && !$json.skip_sending) {
  const response_time = new Date(Date.now() + 1000);
  const formatted_response_time = response_time.toLocaleString('es-CO', {
    year: 'numeric',
    month: '2-digit',
    day: '2-digit',
    hour: '2-digit', 
    minute: '2-digit',
    hour12: false
  });

  let bot_entry = `[${formatted_response_time}] Bot: ${response_message.trim()}`;

  // Add response type if available
  if (response_type && response_type !== 'unknown') {
    bot_entry += ` [${response_type}]`;
  }

  new_entries.push(bot_entry);
}

// Special handling for system events
if ($json.should_restart_conversation || isResetScenario) {
  new_entries.push(`[${formatted_time}] Sistema: ConversaciÃ³n reiniciada`);
}

if ($json.conversation_context && $json.conversation_context.handoff) {
  new_entries.push(`[${formatted_time}] Sistema: Transferido a asesor humano`);
}

if ($json.conversation_context && $json.conversation_context.end_conversation) {
  new_entries.push(`[${formatted_time}] Sistema: ConversaciÃ³n finalizada`);
}

// Combine histories
let updated_history = existing_history;
if (new_entries.length > 0) {
  if (updated_history && updated_history.trim() !== '') {
    updated_history += '\n' + new_entries.join('\n');
  } else {
    updated_history = new_entries.join('\n');
  }
}

// Limit length (keep last 150 lines for enhanced version)
const history_lines = updated_history.split('\n');
if (history_lines.length > 150) {
  updated_history = history_lines.slice(-150).join('\n');
  console.log('âš ï¸ Trimmed conversation history to last 150 lines');
}

console.log('Enhanced history updated with', new_entries.length, 'new entries');
console.log('Final history length:', updated_history.length);

return {
