    ...$json,
    conversation_history: updated_history,
    conversation_history_metadata: {
      last_updated: new Date().toISOString(),
      total_lines: updated_history.split('\n').length,
      entries_added: new_entries.length,
      is_reset_scenario: isResetScenario
    }
  }
};
